class Car {
  String make;
  String model;
  int year;
  String color;
  int speed;

  Car(this.make, this.model, this.year, this.color, this.speed);

  void accelerate(int speedIncrement) {
    speed += speedIncrement;
    if (speed > 120) {
      speed = 120;
    }
  }

  void brake(int speedDecrement) {
    speed -= speedDecrement;
    if (speed < 0) {
      speed = 0;
    }
  }
}
